import { defineStore } from 'pinia';

export const useTaskStore = defineStore('taskStore', {
  state: () => ({
    tasks: [],
  }),
  actions: {
    async getTask() {
      const res = await fetch('/api/tasks');
      const data = await res.json();

      this.tasks = data;
    },
  },
});
