import { createStore } from 'vuex';
import axios from 'axios';

const url = 'http://localhost:3000/';

export default createStore({
  state: {
    tasks: [],
    contacts: [],
  },
  getters: {
    allTasks: (state) => state.tasks,
  },

  actions: {
    //Code for Tasks
    async getTasks({ commit }) {
      const response = await axios.get(url + 'api/tasks');
      commit('setTasks', response.data);
    },
    async addTask({ commit }, _newTask) {
      const response = await axios.post(url + 'api/tasks', { _newTask });

      commit('newTask', response.data);
    },
    async deleteTask({ commit }, id) {
      await axios.delete(url + 'api/tasks/id');
    },
    async filterTask({ commit }, e) {
      console.log(e); //minute 45:44
      const limit = parseInt(
        e.target.options[e.target.options.selectetdIndex].innerText,
      );
      const response = await axios.get(url + `api/tasks?_limit=${limit}`);

      commit('setTask', response.data);
    },
    async updateTask({ commit }, updTask) {
      const response = await axios.put(
        url + `api/tasks/${updTask.id}`,
        updTask,
      );
      console.log(response.data);
      commit(url + 'updTask', response.data);
    },

    //Code for contacts
    async getContacts({ commit }) {
      const response = await axios.get(url + 'api/contacts');
      commit('setContacts', response.data);
    },
    async addContacts({ commit }, _newContacts) {
      const response = await axios.post(url + 'api/contacts', {
        _newContacts,
      });

      commit('newContacts', response.data);
    },
    async deleteContacts({ commit }, id) {
      await axios.delete(url + 'api/contacts/id');
    },
    async filterContacts({ commit }, e) {
      console.log(e); //minute 45:44
      const limit = parseInt(
        e.target.options[e.target.options.selectetdIndex].innerText,
      );
      const response = await axios.get(url + `api/contacts?_limit=${limit}`);

      commit('setContacts', response.data);
    },
    async updateContacts({ commit }, updContacts) {
      const response = await axios.put(
        url + `api/contacts/${updContacts.id}`,
        updContacts,
      );
      console.log(response.data);
      commit(url + 'updContacts', response.data);
    },
  },
  mutations: {
    //Tasks
    setTasks: (state, tasks) => (state.tasks = tasks),
    newTask: (state, task) => state.tasks.unshift(task),
    removeTask: (task, id) =>
      (state.tasks = state.tasks.filter((task) => task.id !== id)),
    updatTask: (state, updTask) => {
      const index = state.task.findIndex((task) => task.id === updTask.id);
      if (index !== -1) {
        state.task.splice(index, 1, updTask);
      }
    },
    //Contacts
    setContacts: (state, contacts) => (state.contacts = contacts),
    newContact: (state, contact) => state.contacts.unshift(contact),
    removeContact: (contact, id) =>
      (state.contacts = state.contacts.filter((contact) => contact.id !== id)),
    updateContacts: (state, updContact) => {
      const index = state.contacts.findIndex(
        (contact) => contact.id === updContact.id,
      );
      if (index !== -1) {
        state.contact.splice(index, 1, updContact);
      }
    },
  },
});
