import axios from 'axios';
export default {
  name: 'UpdateTask',
  data() {
    return {
      text: '',
      day: '',
      contacts: [],
      selected: ''
    };
  },
  methods: {
    onSubmit(e) {
      e.preventDefault();
      if (!this.text) {
        alert('Please add a task');
        return;
      }
      const updateTask = {
        text: this.text,
        day: this.day,
        contact: this.selected
      };
      this.$emit('update-task', updateTask);
      this.text = '';
      this.day = '';
      this.selected = '';
    }
  },
  // Ende von  Methods
  created() {
    axios.get(`/api/contacts/`).then(response => {
      this.contacts = response.data;
    }).catch(error => {
      console.log(error);
    });
  }
};
