export default {
  name: 'AddTask',
  data() {
    return {
      task: '',
      day: '',
      selected: '',
      contacts: []
    };
  },
  methods: {
    onSubmit(e) {
      e.preventDefault();
      if (!this.task || !this.day) {
        alert('Please add a task');
        return;
      }
      const _newTask = {
        task: this.task,
        day: this.day
      };
      this.$emit('add-task', _newTask);
      this.task = '';
      this.day = '';
    }
  }
};
